          	else if(r<67)
	        printf("%s", l337d1c7[i].pool[1]);
	      	else
	        printf("%s", l337d1c7[i].pool[2]);


	      found = 1;
	      break;
	   	 }
		}
		if(!found)
		printf("%c", *yytext);
